INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('NWS19','NWS','Ricerca Personale','PUBLIC','<?xml version="1.0" encoding="UTF-8"?><content id="NWS19" typecode="NWS" typedescr="News"><descr>Ricerca Personale</descr><groups mainGroup="free"><group name="free" /></groups><categories><category id="personale" /></categories><attributes><attribute name="Date" attributetype="Date"><date>20190318</date></attribute><attribute name="Title" attributetype="Text"><text lang="en">Ricerca di personale per il ripianamento di P.O. nell''ambito degli Organismi Internazionali in Italia</text></attribute><attribute name="Abstract" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Allo scopo di soddisfare l''esigenza in titolo è indetta una ricerca di personale da reimpiegare a domanda per il ripianamento di P.O. nell''ambito …]]></hypertext></attribute><attribute name="MainBody" attributetype="Hypertext"><hypertext lang="en"><![CDATA[OGGETTO: RICERCA DI PERSONALE PER IL RIPIANAMENTO DI P.O. NELL''AMBITO DEGLI ORGANISMI INTERNAZIONALI IN ITALIA – ANNO 2019/2020.1. ALLO SCOPO DI SODDISFARE L''ESIGENZA IN TITOLO E'' INDETTA UNA RICERCA DI PERSONALE DA REIMPIEGARE A DOMANDA SULLE POSIZIONI ORGANICHE RIEPILOGATE IN ALL. "A".2. ICOMANDANTIDELPERSONALECHEADERIRA’ALLARICERCAINARGOMENTODOVRANNO: VERIFICAREILPOSSESSO,DAPARTEDEGLISTESSI,DEIREQUISITIRIEPILOGATIINALL."A"E"B"; FAR COMPILARE LA DOMANDA, REDATTA IN CARTA SEMPLICE, CONFORME A QUANTOINDICATO IN ALL. "C"; FAR AGGIORNARE E VALIDARE LA SCHEDA BIOGRAFICA SUL SIGE CENTRALIZZATO; SOLO PER IL PERSONALE IN SERVIZIO PRESSO EDR INTERFORZE CHE NON HANNO ACCESSO AL SIGE CENTRALIZZATO COMPILARE LA SCHEDA BIOGRAFICA IN ALL. "D" (ALL. "E" EVENTUALE) AGGIORNATA IN OGNI SUA PARTE; NON INOLTRARE LE ISTANZE DEL PERSONALE NON IN POSSESSO DEI REQUISITI PROFESSIONALI DI BASE COSI'' COME RIPORTATO NEI CITATI ALL. "A" E "B".3. LASUDDETTADOCUMENTAZIONE: SARA'' PUBBLICATA SULLA PAGINA INTRANET DEL DIPARTIMENTO IMPIEGO DEL PERSONALEALL’INDIRIZZO: HTTP://WWW.SME.ESERCITO.DIFESA.IT - FILE DI PERCORSO: PERSONALE –DOCUMENTI DISPONIBILI. DOVRA'' ESSERE TRASMESSA ENTRO L’8 MARZO 2019, IN FORMATO .PDF, TRAMITE SISTEMAADHOC AD UNO DEI SEGUENTI INDIRIZZI DI POSTA ELETTRONICA:  EIPERSONALE@ESERCITO.DIFESA.IT; EIPERSONALE@POSTACERT.DIFESA.IT.4. SISPECIFICACHEILPERSONALESELEZIONATO: QUALORA NECESSARIO SARA’ AVVIATO AD UNO SPECIFICO CORSO DI LINGUA INGLESE,PRESSO LA SLEE, AL FINE DI ACQUISIRE IL LIVELLO LINGUISTICO PREVISTO PER UN IMPIEGO PRESSO GLI ORGANISMI INTERNAZIONALI IN ITALIA. SI PRECISA CHE IL CONSEGUIMENTO DI DETTO LIVELLO SARA’ CONSIDERATO DISCRIMINANTE PER L’IMPIEGO IN ARGOMENTO; DOVRA’ INOLTRARE SPECIFICA ISTANZA DI TRASFERIMENTO.5. LAPRESENTERICERCADOVRA''ESSEREDIRAMATA,SENZAALCUNAMODIFICA,FINOALIVELLOCOMPAGNIA/BATTERIA/SQUADRONE PER LA PIU'' AMPIA DIFFUSIONE.FIRMATO IL CAPO UFFICIO IMPIEGO SOTTUFFICIALI.]]></hypertext></attribute><attribute name="Picture" attributetype="Image" /><attribute name="Caption" attributetype="Text" /><list name="Links" attributetype="Monolist" nestedtype="Link" /><list name="Attaches" attributetype="Monolist" nestedtype="Attach"><attribute name="Attaches" attributetype="Attach"><resource resourcetype="Attach" id="20" lang="en" /><text lang="en">ALL A - ricerca 1 - 2019-2020.pdf</text></attribute><attribute name="Attaches" attributetype="Attach"><resource resourcetype="Attach" id="21" lang="en" /><text lang="en">All B - ricerca 1 - 2019.pdf</text></attribute><attribute name="Attaches" attributetype="Attach"><resource resourcetype="Attach" id="22" lang="en" /><text lang="en">All C EDIT.pdf</text></attribute><attribute name="Attaches" attributetype="Attach"><resource resourcetype="Attach" id="23" lang="en" /><text lang="en">All D.doc</text></attribute><attribute name="Attaches" attributetype="Attach"><resource resourcetype="Attach" id="24" lang="en" /><text lang="en">All E.docx<