INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('NWS42','NWS','Prosegue l''impegno della Forza Armata a tutela del benessere del proprio personale.','PUBLIC','<?xml version="1.0" encoding="UTF-8"?><content id="NWS42" typecode="NWS" typedescr="News"><descr>Prosegue l''impegno della Forza Armata a tutela del benessere del proprio personale.</descr><groups mainGroup="free"><group name="free" /></groups><categories><category id="benessere" /></categories><attributes><attribute name="Date" attributetype="Date"><date>20190318</date></attribute><attribute name="Title" attributetype="Text"><text lang="en">Prosegue l''impegno della Forza Armata a tutela del benessere del proprio personale.</text></attribute><attribute name="Abstract" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Nel 2018 è stato possibile accogliere, sulla base di criteri di trasparenza e meritocrazia 1.058 richieste di trasferimento da parte dei graduati dell''Esercito, con un incremento pari al 5% rispetto al 2017. Un processo attuato nell''ambito della disponibilità espressa da detto personale al movimento presso sedi loro gradite e all''avvicendamento reciproco, sempre coniugando le esigenze organico funzionali della Forza Armata con le aspirazioni di impiego e professionali dei militari, e che ne permetterà, di fatto, il ricongiungimento con i propri affetti e/o avvicinamento ai luoghi di loro interesse.  ]]></hypertext></attribute><attribute name="MainBody" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Nel 2018 è stato possibile accogliere, sulla base di criteri di trasparenza e meritocrazia 1.058 richieste di trasferimento da parte dei graduati dell''Esercito, con un incremento pari al 5% rispetto al 2017. Un processo attuato nell''ambito della disponibilità espressa da detto personale al movimento presso sedi loro gradite e all''avvicendamento reciproco, sempre coniugando le es