INSERT INTO contents (contentid,contenttype,descr,status,workxml,created,lastmodified,onlinexml,maingroup,currentversion,lasteditor,firsteditor) VALUES ('NWS47','NWS','I lavori di rifacimento','PUBLIC','<?xml version="1.0" encoding="UTF-8"?><content id="NWS47" typecode="NWS" typedescr="News"><descr>I lavori di rifacimento</descr><groups mainGroup="free"><group name="free" /></groups><categories><category id="in_evidenza" /></categories><attributes><attribute name="Date" attributetype="Date"><date>20190318</date></attribute><attribute name="Title" attributetype="Text"><text lang="en">Terminati, alla Cecchignola, i lavori di rifacimento delle infrastrutture per il personale del''Operazione "Strade Sicure"</text></attribute><attribute name="Abstract" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Questa mattina il Ministro della Difesa Elisabetta Trenta, accompagnata dal Capo di Stato Maggiore dell''Esercito, Generale di Corpo d''Armata Salvatore Farina, ha visitato gli alloggi appena ristrutturati presso alcune caserme della Città Militare "Cecchignola" finalizzati a migliorare le condizioni alloggiative del personale impiegato nell''Operazione "Strade Sicure" a Roma.]]></hypertext></attribute><attribute name="MainBody" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Questa mattina il Ministro della Difesa Elisabetta Trenta, accompagnata dal Capo di Stato Maggiore dell''Esercito, Generale di Corpo d''Armata Salvatore Farina, ha visitato gli alloggi appena ristrutturati presso alcune caserme della Città Militare "Cecchignola" finalizzati a migliorare le condizioni alloggiative del personale impiegato nell''Operazione "Strade Sicure" a Roma.Il Ministro Trenta, durante l''incontro con il personale militare e civile del Comando comprensorio, ha sottolineato che "l''intervento rientra nell''attuazione del più ampio e ambizioso studio "Grandi Infrastrutture – Caserme Verdi", presentato dallo Stato Maggiore dell''Esercito e voluto dalla Difesa per mettere a disposizione del personale infrastrutture di nuova generazione, efficienti, funzionali, pienamente rispondenti alle normative vigenti, comprensive di aree addestrative, poli alloggiativi per il personale militare e relative famiglie e strutture sportive e socio ricreative aperte anche alla collettività".I lavori, eseguiti dal Reparto Operativo del Genio Infrastrutturale (ROGI), hanno visto la messa in opera di interventi di manutenzione di alcune palazzine che hanno consentito di  incrementare la capacità alloggiativa con ulteriori 460 posti letto. Questo intervento fa seguito a quelli già realizzati negli ultimi mesi a Roma, Milano e Cesana Torinese (TO) che hanno consentito l''ammodernamento di oltre 100 alloggi.Tali misure si inseriscono nell''attuazione delle linee programmatiche illustrate dal Generale Farina alle Commissioni Difesa riunite di Camera e Senato ed in particolare per quanto riguarda gli aspetti legati al benessere e alla sicurezza del personale che possa operare e vivere in infrastrutture moderne e sostenibili. In tale contesto, sono state avviate anche le attività propedeutiche per la realizzazione di alcuni progetti pilota al Nord, Centro e Sud Italia.L''avvio di questo innovativo e moderno studio, dal basso impatto finanziario trattandosi di un piano ad ampio respiro e concepito per rinnovare un parco infrastrutturale costituito da immobili realizzati da più di 70 anni, consentirà di intervenire con risolutezza e senza indugi avviando un processo "virtuoso" che permetta di poter disporre di un parco infrastrutturale completamente rinnovato con conseguenti ridotti costi di manutenzione, ad alto efficiente energetico e con materiali ecosostenibili. Il progetto, peraltro, risulta costo-efficace dal punto di vista economico, anche in relazione al contenimento, in prospettiva, dei costi di funzionamento e dei vantaggi per il personale insiti nell''utilizzo di standard qualitativamente superiori.]]></hypertext></attribute><attribute name="Picture" attributetype="Image" /><attribute name="Caption" attributetype="Text" /><list name="Links" attributetype="Monolist" nestedtype="Link" /><list name="Attaches" attributetype="Monolist" nestedtype="Attach" /></attributes><status>PUBLIC</status><version>5.0</version><firstEditor>admin</firstEditor><lastEditor>admin</lastEditor><created>20190318132422</created><lastModified>20190318160721</lastModified></content>','20190318132422','20190318160721','<?xml version="1.0" encoding="UTF-8"?><content id="NWS47" typecode="NWS" typedescr="News"><descr>I lavori di rifacimento</descr><groups mainGroup="free"><group name="free" /></groups><categories><category id="in_evidenza" /></categories><attributes><attribute name="Date" attributetype="Date"><date>20190318</date></attribute><attribute name="Title" attributetype="Text"><text lang="en">Terminati, alla Cecchignola, i lavori di rifacimento delle infrastrutture per il personale del''Operazione "Strade Sicure"</text></attribute><attribute name="Abstract" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Questa mattina il Ministro della Difesa Elisabetta Trenta, accompagnata dal Capo di Stato Maggiore dell''Esercito, Generale di Corpo d''Armata Salvatore Farina, ha visitato gli alloggi appena ristrutturati presso alcune caserme della Città Militare "Cecchignola" finalizzati a migliorare le condizioni alloggiative del personale impiegato nell''Operazione "Strade Sicure" a Roma.]]></hypertext></attribute><attribute name="MainBody" attributetype="Hypertext"><hypertext lang="en"><![CDATA[Questa mattina il Ministro della Difesa Elisabetta Trenta, accompagnata dal Capo di Stato Maggiore dell''Esercito, Generale di Corpo d''Armata Salvatore Farina, ha visitato gli alloggi appena ristrutturati presso alcune caserme della Città Militare "Cecchignola" finalizzati a migliorare le condizioni alloggiative del personale impiegato nell''Operazione "Strade Sicure" a Roma.Il Ministro Trenta, durante l''incontro con il personale militare e civi