INSERT INTO actionlogrecords (id,username,actiondate,namespace,actionname,parameters,activitystreaminfo,updatedate) VALUES (63,'admin','2019-03-18 15:29:37','/do/jacms/Content','saveAndApprove','entandoaction:saveAndApprove=SubmitHypertext:it_MainBody=Hypertext:en_Abstract=Nel 2018 è stato possibile accogliere, sulla base di criteri di trasparenza e meritocrazia 1.058 richieste di trasferimento da parte dei graduati dell''Esercito, con un incremento pari al 5% rispetto al 2017. Un processo attuato nell''ambito della disponibilità espressa da detto personale al movimento presso sedi loro gradite e all''avvicendamento reciproco, sempre coniugando le esigenze organico funzionali della Forza Armata con le aspirazioni di impiego e professionali dei militari, e che ne permetterà, di fatto, il ricongiungimento con i propri affetti e/o avvicinamento ai luoghi di loro interesse.Date:Date=18/03/2019Hypertext:it_Abstract=Text:it_Caption=treeNodesToOpen=homeText:en_Title=Prosegue l’impegno della Forza Armata a tutela del benessere del proprio personale.Text:en_Caption=Hypertext:en_MainBody=Nel 2018 è stato possibile accogliere, sulla base di criteri di trasparenza e meritocrazia 1.058 richieste di trasferimento da parte dei graduati dell''Esercito, con un incremento pari al 5% rispetto al 2017. Un processo attuato nell''ambito della disponibilità espressa da detto personale al movimento presso sedi loro gradite e all''avvicendamento reciproco, sempre coniugando le esigenze organico funzionali della Forza Armata con le aspirazioni di impiego e professionali dei militari, e che ne permetterà, di fatto, il ricongiungimento con i propri affetti e/o avvicinamento ai luoghi di loro interesse.__multiselect_extraGroupNames=descr=Prosegue l''impegno della Forza Armata a tutela del benessere del proprio personale.contentOnSessionMarker=NWS_editContent_NWS42Text:it_Title=status=','<?xml version="1.0" encoding="UTF-8" standalone="yes"?><activityStreamInfo>    <objectTitles>        <e